mapper.select:
select
  pid, pname, price, flag, picturePath, category_id, descri, remark
from example_goods.goods


        and pid = #{pid}


        and pname = #{pname}


        and price = #{price}


        and flag = #{flag}


        and picturePath = #{picturepath}


        and category_id = #{categoryId}


        and descri = #{descri}


        and remark = #{remark}

select
  pid, pname, price, flag, picturePath, category_id, descri, remark
from example_goods.goods


        and pid = #{pid}


        and pname = #{pname}


        and price = #{price}


        and flag = #{flag}


        and picturePath = #{picturepath}


        and category_id = #{categoryId}


        and descri = #{descri}


        and remark = #{remark}


limit 0,1

select * from uploadfile.goods


        pid = #{pid}


        and pname = #{pname}


        and price = #{price}


        and flag = #{flag}


        and categoryId = #{categoryId}

select g.*
from goods g,
商品订单 s
where s.pid = g.pid
and s.MaiJiaID =#{maijiaid}

    and s.state = #{state}


    and s.isEnd = #{isEnd}

limit #{current},#{size} ;
